import WalletIcon from '@/components/icons/money/wallet/wallet';
import { BlurView } from 'expo-blur';
import Checkbox from 'expo-checkbox';
import { LinearGradient } from "expo-linear-gradient";
import { useRouter } from "expo-router";
import { StatusBar } from "expo-status-bar";
import { useEffect, useState } from "react";
import { Image, Pressable, ScrollView, Text, View } from "react-native";
import Svg, { ClipPath, Defs, Ellipse, G, Path } from "react-native-svg";

export default function ReserveTrip() {
    const router = useRouter();

    const [loading, setLoading] = useState(false)
    const [success, setSuccess] = useState(false)

    useEffect(() => {
        if (loading) {

            setTimeout(() => {
                setLoading(false)
                setSuccess(true)
            }, 1000);
        }
    }, [loading])

    return (
        <LinearGradient
            colors={["#55B6C8", "#1B77F2"]}
            start={{ x: 0.5, y: 0 }}
            end={{ x: 0.5, y: 1 }}
            style={{ flex: 1 }}
        >
            <StatusBar style="light" />
            {
                success && (
                    <BlurView intensity={25} className="absolute flex-1 inset-0 z-50 items-center justify-center bg-[#FFFFFF6B]">
                        <View className="items-center justify-center p-10 rounded-2xl bg-white w-[90%] gap-[16px]">
                            <SUCCESS />
                            <Text className="font-poppins-bold text-[#1B77F2] text-[20px]">
                                Payment Successfull!
                            </Text>
                            <Text className="text-center">
                                Successfully made payment and hotel booking
                            </Text>

                            <View className="flex-row justify-between w-full">
                                <Pressable onPress={() => setSuccess(false)} className="w-[45%] rounded-[16px] py-[16px] bg-[#DADFB5] border-[1.5px] border-[#94A604] items-center">
                                    <Text className='text-[16px] font-poppins-semibold text-[#94A604]'>Back</Text>
                                </Pressable>

                                <Pressable onPress={() => router.push("/(private)/(tabs)/trip")} className="w-[45%] rounded-[16px] py-[16px] bg-[#94A604] border-[1.5px] border-[#94A604] items-center">
                                    <Text className='text-[16px] font-poppins-semibold text-white'>Go to my trip</Text>
                                </Pressable>
                            </View>
                        </View>
                    </BlurView>

                )
            }

            {
                loading && (
                    <BlurView intensity={25} className="absolute flex-1 inset-0 z-50 items-center justify-center bg-[#FFFFFF6B]">
                        <View className="items-center justify-center p-10 rounded-2xl">
                            <Image
                                source={require("@/assets/images/loader.png")}
                                className="w-[150px] h-[150px]"
                            />
                            <Text className="font-poppins-bold text-[#1B77F2] text-[20px]">
                                Ready for new adventures...
                            </Text>
                        </View>
                    </BlurView>
                )
            }

            <ScrollView
                contentContainerStyle={{ flexGrow: 1 }}
                showsVerticalScrollIndicator={false}
            >
                <View className="px-[20px] pt-[50px] gap-[12px] items-center">
                    <View className="w-full">
                        <Pressable className="flex flex-row gap-[10px] items-center" onPress={() => router.push("/(private)/(tabs)/profile")}>
                            <ARROW_BACK />
                            <Text className="font-poppins-bold text-[14px] text-center font-[700] text-white">
                                Back
                            </Text>
                        </Pressable>
                    </View>

                    <Text className="font-poppins-bold text-[20px] text-center font-[700] text-white">
                        You are about to book
                    </Text>
                    <Text className="font-poppins-bold text-[20px] text-center font-[700] text-white">
                        your trip!
                    </Text>
                </View>

                <View className="bg-white flex-1 rounded-t-[40px] mt-[30px] pt-[30px] gap-[20px] px-[20px] pb-[30px] w-full">
                    <Text className='font-poppins-bold font-700 text-[16px] text-text-600 leading-[19px]'>
                        Book everything
                    </Text>

                    <View className="bg-white shadow-lg shadow-[#97C3FF66] rounded-[16px] gap-[16px]">
                        <View className="p-[20px] gap-[16px]">
                            <View className='flex flex-row items-center gap-2'>
                                <LOVE_HOTEL />
                                <View className='flex'>
                                    <Text className="font-poppins-medium text-[14px] text-[#646464]">
                                        Hotel Boutique X
                                    </Text>
                                    <Text className='text-[#8A8A8A] font-poppins-medium text-[12px]'>
                                        3 nights
                                    </Text>
                                </View>
                            </View>
                            <View className='flex flex-row items-center gap-2'>
                                <DESERT_ISLAND />
                                <View className='flex'>
                                    <Text className="font-poppins-medium text-[14px] text-[#646464]">
                                        Women's Islands Tour
                                    </Text>
                                    <Text className='text-[#8A8A8A] font-poppins-medium text-[12px]'>
                                        2 Pax
                                    </Text>
                                </View>
                            </View>
                            <View className='flex flex-row items-center gap-2'>
                                <POT_FOOD />
                                <View className='flex'>
                                    <Text className="font-poppins-medium text-[14px] text-[#646464]">
                                        Dinner at the Muellecito
                                    </Text>
                                </View>
                            </View>
                            <View className='flex flex-row items-center gap-2'>
                                <WORLD_MAP />
                                <View className='flex'>
                                    <Text className="font-poppins-medium text-[14px] text-[#646464]">
                                        Tour Xplor
                                    </Text>
                                </View>
                            </View>
                        </View>

                        <View className="bg-[#D9E6F8] w-full px-[20px] py-[16px] gap-[10px]">
                            <View className="flex flex-row items-center justify-between">
                                <Text className="text-[#444444] font-poppins-bold text-[14px]">
                                    Subtotal
                                </Text>
                                <Text className="text-[#444444] font-poppins-bold text-[14px]">
                                    $ 3,940
                                </Text>
                            </View>

                            <View className="flex flex-row items-center justify-between">
                                <Text className="text-[#444444] font-poppins-bold text-[14px]">
                                    Discount applied
                                </Text>
                                <Text className="text-[#444444] font-poppins-bold text-[14px]">
                                    -$ 650
                                </Text>
                            </View>

                            <View className="border border-[#444444]" />

                            <View className="flex flex-row items-center justify-between">
                                <Text className="text-[#444444] font-poppins-bold text-[20px]">
                                    Total
                                </Text>
                                <Text className="text-[#444444] font-poppins-bold text-[20px]">
                                    $ 3,940
                                </Text>
                            </View>
                        </View>
                    </View>

                    <View className='gap-4'>
                        <Text className='font-poppins-bold font-700 text-[16px] text-text-600 leading-[19px]'>
                            Payment Details
                        </Text>

                        <Text className='font-poppins font-400 text-[14px] text-text-600 leading-[19px]'>
                            Choose Payment Options
                        </Text>

                        <View className='flex gap-[16px]'>
                            <View className="flex flex-row p-[18px] rounded-[16px] gap-4 shadow-lg shadow-[#97C3FF66] bg-white">
                                <Checkbox />
                                <MASTERCARD />
                                <Text className="flex-1 text-[#646464] text-[12px] font-poppins-bold">Mastercard ending in 1234</Text>
                            </View>

                            <View className="flex flex-row p-[18px] rounded-[16px] gap-4 shadow-lg shadow-[#97C3FF66] bg-white">
                                <Checkbox />
                                <WalletIcon size='medium' color='#646464' />
                                <Text className="flex-1 text-[#646464] text-[12px] font-poppins-bold">Mastercard ending in 1234</Text>
                            </View>

                            <View className="flex flex-row p-[18px] rounded-[16px] gap-4 shadow-lg shadow-[#97C3FF66] bg-white">
                                <Checkbox />
                                <WalletIcon size='medium' color='#646464' />
                                <Text className="flex-1 text-[#646464] text-[12px] font-poppins-bold">Paypal</Text>
                            </View>
                        </View>
                    </View>

                    <View className="flex flex-row gap-2 items-center">
                        <PERCENTAGE />
                        <Text className="flex-1 text-[#1B77F2] font-poppins-semibold text-[16px]">Enter coupon</Text>
                    </View>

                    <Pressable onPress={() => setLoading(true)} className="flex flex-row items-center justify-center gap-[4px] py-[12px] w-full bg-[#1B77F2] rounded-[12px]">
                        <Text className="text-white font-poppins-semibold font-[600] text-[16px] ">Confirm and pay</Text>
                    </Pressable>
                </View>
            </ScrollView>
        </LinearGradient >
    )
}


